import { menuState } from '../state/Atoms';
import { useRecoilState } from 'recoil';

function Modify({ categories, db }) {
     const [menu, setMenuState] = useRecoilState(menuState);
     const optionsArray = [];

     for (const item of db) {
          if (item.menu === 'modify') {
               for (const addon of item.addons) {
                    if (addon.category === menu.currentCategory) {
                         optionsArray.push(addon);
                    }
               }
          }
     }

     const checkRequired = () => {
          for (const option of menu.selection.options) {
               for (const current of menu.pending.modify) {
                    if (option.category === current.category) {
                         console.log(current, option);
                    }
               }
          }
     };

     const addItem = (item) => {
          setMenuState({
               ...menu,
               pending: {
                    ...menu.pending,
                    modify: [...menu.pending.modify, item],
               },
          });
     };

     checkRequired();

     return optionsArray.map((item, i) => (
          <div onClick={() => addItem(item)} key={i} className='menu_item'>
               <div className='menuItemText'>{item.food}</div>
          </div>
     ));
}

export default Modify;
