import { atom } from 'recoil';

export const menuState = atom({
     key: 'menuState',
     default: {
          isSelected: false,
          selection: {},
          currentCategory: 'Burgers',
          pending: [],
          active: [],
     },
});

export const cartState = atom({
     key: 'cartState',
     default: {},
});
