import '../../CSS/Menu.css';
import MenuItem from './MenuItem';
import MenuCategories from './MenuCategories';
import Modify from './Modify';
import { useState } from 'react';
import { selectionState } from '../state/Atoms';
import { useRecoilState } from 'recoil';

function Menu(props) {
     const database = props.database;
     const [currentCategory, setCurrentCategory] = useState('Burgers');
     const [isSelected, setIsSelected] = useState(false);
     let categoryArray = [];
     let menuArray = [];
     let options;

     console.log(categoryArray);

     // Loops through menu array
     for (const item of database) {
          //pushes menu category to categoryArray
          if (item.food && !isSelected) {
               categoryArray.push({
                    category: item.category,
                    color: item.color,
               });
          } else if (item.addons && isSelected) {
               for (const option of item.options) {
                    categoryArray.push({
                         category: option.option,
                         color: option.color,
                    });
               }
               options = item;
          }
          //Pushes menu items that are in currentCategory to menuArray
          if (item.category === currentCategory) {
               menuArray.push(item);
          }
     }

     categoryArray = [
          ...new Map(
               categoryArray.map((item) => [item.category, item])
          ).values(),
     ];

     return (
          <div className='menu'>
               <div className='menu_list'>
                    {menuArray.map((item) => (
                         <MenuItem
                              item={item}
                              setSelected={setIsSelected}
                              setCurrent={setCurrentCategory}
                              key={item.id}
                         ></MenuItem>
                    ))}
               </div>
               {
                    <MenuCategories
                         categoryArry={categoryArray}
                         setCurrent={setCurrentCategory}
                         currentCategory={currentCategory}
                    ></MenuCategories>
               }
          </div>
     );
}

export default Menu;
