import '../CSS/Menu.css';
import MenuItem from './MenuItem';
import { useState, useEffect } from 'react';

function Menu(props) {
     const database = props.database;
     const [currentCategory, setCurrentCategory] = useState('burgers');
     const [currentMenu, setCurrentMenu] = useState();
     const [menuCategories, setMenuCategories] = useState([]);

     useEffect(() => {
          let categoryArray = [];
          for (const item of database) {
               categoryArray.push(item.category);
          }
          categoryArray = categoryArray.filter((item, i, array) => {
               return array.indexOf(item) === i;
          });
          setMenuCategories(categoryArray);
     }, [database]);

     console.log(menuCategories);

     return (
          <div className='menu'>
               <div className='menu_list'>
                    {database.map((item) => MenuItem(item))}
               </div>
               <div className='menu_categories'>
                    <div>
                         {menuCategories.map((item) => (
                              <div className='menu_categoryBtn'>{item}</div>
                         ))}
                    </div>
               </div>
          </div>
     );
}

export default Menu;
