import { useState } from 'react';
function MenuCategories(props) {
     const categories = props.categoryArry;
     const currentCategory = props.currentCategory;
     const [highlight, setHighlight] = useState('transparent');
     const changeStyle = (cat) => {
          setHighlight('white');
          console.log(cat);
     };
     return (
          <div className='menu_categories'>
               {categories.map((item, i) => {
                    return (
                         <div
                              onClick={(e) => {
                                   props.setCurrent(item.category);
                                   changeStyle(item.category);
                              }}
                              className='menu_categoryBtn'
                              style={{
                                   backgroundColor: item.color,
                                   borderColor: highlight,
                              }}
                              key={i}
                         >
                              {item.category}
                         </div>
                    );
               })}
          </div>
     );
}

export default MenuCategories;
