import { useRecoilState } from 'recoil';
import { selectionState } from '../state/Atoms';

function MenuItem(props) {
     const [selection, setSelection] = useRecoilState(selectionState);
     const item = props.item;
     const select = props.setSelected;
     const current = props.setCurrent;
     function checkSides() {
          if (item.sides > 0) {
               select(true);
               setSelection(item);
               current('Sides');
          }
          setSelection(item);
     }
     return (
          <div
               className='menu_item'
               onClick={() => {
                    checkSides();
               }}
          >
               <div className='menuItemText'>{item.food}</div>
          </div>
     );
}

export default MenuItem;
